# Return ordinary least squares regression solution coefficientsfunction [ols_coeffs] = get_ols_solution_coeffs(X, Y)  ols_coeffs = inverse(X' * X)*X'*Y;end# Return ridge regression solution coefficientsfunction [ridge_regression_coeffs] = get_ridge_regression_coeffs(X, Y, s)  ridge_regression_coeffs = inverse(X'*X + s^2*eye(size(X)(2)))*X'*Y;end# Return the regression errorfunction [regression_error] = get_regression_error(X, Y, regression_coeffs)  regression_error = norm(Y - X*regression_coeffs, 2);end s = [0.1, 0.3, 0.5, 1.0, 2.0];# Load the data from fileload('X.dat');load('Y.dat');# Change X for including the bias termX(:, end+1) = ones(size(X)(1),1);# Run ordinary least squares regressionols_coeffs = get_ols_solution_coeffs(X, Y);# Report OLS regression errorprintf("OLS Regression error is %f\n", get_regression_error(X, Y, ols_coeffs));# Run ridge regression for each value of s and report errorfor regularizer_index = 1:size(s)(2)  ridge_regression_coeffs = get_ridge_regression_coeffs(X, Y, s(1, regularizer_index));  printf("Ridge Regression error for regularizer value %f is %f\n", s(1, regularizer_index), get_regression_error(X, Y, ridge_regression_coeffs));endfor